mapper.select:
SELECT 
  FROM BASIC_XCURR
WHERE
    CURRE = #{curre}
and
    TDATE = #{tdate}

mapper.delete:
delete from BASIC_XCURR
    WHERE
        CURRE = #{item.curre}
    and
        TDATE = #{item.tdate}

delete.foreach:
delete from BASIC_XCURR
    WHERE
         CURRE = #{item.curre}
    and
         TDATE = #{item.tdate}
