mapper.select:
SELECT 
  FROM BASIC_XITEM
WHERE
    CODE = #{code}
and
    BUMO = #{bumo}

mapper.delete:
delete from BASIC_XITEM
    WHERE
        CODE = #{item.code}
    and
        BUMO = #{item.bumo}

delete.foreach:
delete from BASIC_XITEM
    WHERE
         CODE = #{item.code}
    and
         BUMO = #{item.bumo}
