mapper.select:
SELECT 
  FROM BASIC_XPRTS
WHERE
    CODE = #{code}
and
    KCODE = #{kcode}
and
    KEDA = #{keda}
and
    BUMO = #{bumo}
and

mapper.delete:
delete from BASIC_XPRTS
WHERE
        CODE = #{item.code}
        and
        KCODE = #{item.kcode}
        and
        KEDA = #{item.keda}
        and
        BUMO = #{item.bumo}
        and

delete.foreach:
delete from BASIC_XPRTS
    WHERE
         CODE = #{item.code}
    and
         KCODE = #{item.kcode}
    and
         KEDA = #{item.keda}
    and
         BUMO = #{item.bumo}
    and
